queries.query:

UPDATE new_configuration_lang SET `value` = "" WHERE id_configuration = 269 AND id_lang = 7;
INSERT INTO new_configuration (name, value)
    SELECT * FROM (SELECT 'PS_LOGO_INVOICE', 'logo_invoice-1.jpg') AS tmp
    WHERE NOT EXISTS (
        SELECT name FROM new_configuration WHERE name = 'PS_LOGO_INVOICE'
    ) LIMIT 1;
UPDATE new_configuration SET `value` = "logo_invoice-1.jpg" WHERE name = "PS_LOGO_INVOICE";
